import React from 'react';
import axios from 'axios';
import './TodoList.css';
import checkedImage from './images/checked.png';
import unCheckedImage from './images/unchecked.png';

const TodoList = ( {todos,fetchTodos } ) => {
   const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/todos/${id}`);
      fetchTodos(); // Refresh the todo list
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const handleToggleComplete = async (id, completed) => {
    try {
      await axios.put(`http://localhost:5000/api/todos/${id}`, { completed: !completed });
      fetchTodos(); // Refresh the todo list
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  };

  return (
    <div className='container'>
     <div className='todo-app'>
      <ul id='list-container'>
        {todos.map((todo) => (
          <li key={todo._id}>
            <input
              type="checkbox"
              checked={todo.completed}
              onChange={() => handleToggleComplete(todo._id, todo.completed)}
            />
            <span style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
              {todo.title}
            </span>
            <button onClick={() => handleDelete(todo._id)} id='deletebutton'>Delete</button>
          </li>
        ))}
      </ul>
      </div>
    </div>
  );
};

export default TodoList;
